pragma solidity ^0.8.17;

import "../token/IDXBL.sol";
import "./IPriceFeed.sol";

library VaultStorage {

    bytes32 constant VAULT_STORAGE_KEY = 0xbfa76ec2967ed7f8d3d40cd552f1451ab03573b596bfce931a6a016f7733078c;

    /****************************************************************************
     * Initialization Config Settings
     *****************************************************************************/
    //mint rate bucket
    struct MintRateRangeConfig {
        uint16 minMMVolume;
        uint16 maxMMVolume;
        uint rate;
    }

    //fee token and its associated chainlink feed
    struct FeeTokenConfig {
        address[] feeTokens;
        address[] priceFeeds;
    }

    //initialize config to intialize storage
    struct VaultConfig {

        //the address of the wrapped native token
        address wrappedNativeToken;

        //address of the multisig that will administer this vault
        address adminMultiSig;


        //seconds for any timelock-based changes
        uint32 timelockSeconds;

        //starting volume needed to mint a single DXBL token. This increases
        //as we get closer to reaching the daily goal
        uint baseMintThreshold;

        //initial rate ranges to apply
        MintRateRangeConfig[] rateRanges;

        //set of fee token/price feed pairs to initialize with
        FeeTokenConfig feeTokenConfig;
    }

    /****************************************************************************
     * Stored Settings
     *****************************************************************************/
    //stored mint rate range
    struct MintRateRange {
        uint16 minMMVolume;
        uint16 maxMMVolume;
        uint rate;
        uint index;
    }

    //price feed for a fee token
    struct PriceFeed {
        IPriceFeed feed;
        uint8 decimals;
    }

    /*****************************************************************************************
     * STORAGE
    ******************************************************************************************/


    struct VaultData {
        //whether the vault is paused
        bool paused;

        //admin multi sig
        address adminMultiSig;

        //token address
        IDXBL dxbl;

        //dexible settlement contract that is allowed to call the vault
        address dexible;

        //wrapped native asset address for gas computation
        address wrappedNativeToken;

        //pending migration to new vault
        address pendingMigrationTarget;

        //time before migration allowed
        uint32 timelockSeconds;

        //base volume needed to mint a single DXBL token. This increases
        //as we get closer to reaching the daily goal
        uint baseMintThreshold;

        //current daily volume adjusted each hour
        uint currentVolume;

        //to compute what hourly slots to deduct from 24hr window
        uint lastTradeTimestamp;

        //can migrate the contract to a new vault after this time
        uint migrateAfterTime;

        //all known fee tokens. Some may be inactive
        IERC20[] feeTokens;

        //the current volume range we're operating in for mint rate
        MintRateRange currentMintRate;

        //The ranges of 24hr volume and their percentage-per-MM increase to 
        //mint a single token
        MintRateRange[] mintRateRanges;

        //hourly volume totals to adjust current volume every 24 hr slot
        uint[24] hourlyVolume;

        //fee token decimals
        mapping(address => uint8) tokenDecimals;

        //all allowed fee tokens mapped to their price feed address
        mapping(address => PriceFeed) allowedFeeTokens;
    }

    /**
     * If a migration occurs from the V1 vault to a new vault, this structure is forwarded
     * after all fee token balances are transferred. It is expected that the new vault will have
     * its fee token, minting rates, and starting mint rates mapped out as part of its deployment.
     * The migration is intended to get the new vault into a state where it knows the last 24hrs
     * of volume and can pick up where this vault leaves off but with new settings and capabilities.
     */
    struct VaultMigrationV1 {
        //current daily volume adjusted each hour
        uint currentVolume;

        //to compute what hourly slots to deduct from 24hr window
        uint lastTradeTimestamp;

        //hourly volume totals to adjust in new contract
        uint[24] hourlyVolume;

        //the current volume range we're operating in for mint rate
        MintRateRange currentMintRate;
    }

    function load() internal pure returns (VaultData storage ds) {
        assembly { ds.slot := VAULT_STORAGE_KEY }
    }
}","keccak256":"0xb7466789c7979d005166a1398149d006d226f708342f25ce5167b1e299ac334a","license":"Unlicense"},"contracts/vault/baseContracts/ComputationalView.sol":{"content":"//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.17;

import "../VaultStorage.sol";
import "../IComputationalView.sol";
import "../../common/LibConstants.sol";

contract ComputationalView is IComputationalView {

    function convertGasToFeeToken(address feeToken, uint gasCost) public view returns (uint){
        VaultStorage.VaultData storage rs = VaultStorage.load();

        if(feeToken == rs.wrappedNativeToken) {
            //already in native units
            return gasCost;
        }
        uint np = _getPrice(rs.allowedFeeTokens[rs.wrappedNativeToken]);
        uint ftp = _getPrice(rs.allowedFeeTokens[feeToken]);
        uint ftpNative = (np*LibConstants.PRICE_PRECISION)/ftp;
        uint ftpUnits = (ftpNative * gasCost) / LibConstants.PRICE_PRECISION;
        return (ftpUnits * (10**rs.tokenDecimals[feeToken])) / 1e18; //native is always 18decs
    }

    function estimateRedemption(address rewardToken, uint dxblAmount) public view returns(uint){
        VaultStorage.VaultData storage rs =VaultStorage.load();
        uint nav = currentNavUSD();
         //convert nav to price-precision units
        nav = (nav * LibConstants.PRICE_PRECISION) / LibConstants.USD_PRECISION;

        //we need to know the value of each token in rewardToken units
        //start by getting the USD price of reward token
        uint ftUSD = feeTokenPriceUSD(rewardToken);

        uint8 ftDecs = rs.tokenDecimals[rewardToken];

        //Divide nav of each token by the price of each reward token expanding 
        //precision to include the fee-token decimals
        uint ftUnitPrice = (nav*(10**ftDecs))/ftUSD;

        //compute how much rewardToken to withdraw based on unit price of each DXBL
        //in fee-token units. Have to remove the dexible token precision (18)
        return (dxblAmount * ftUnitPrice)/1e18;
    }

    function feeTokenPriceUSD(address feeToken) public view returns (uint){
        VaultStorage.VaultData storage rs = VaultStorage.load();
        VaultStorage.PriceFeed storage pf = rs.allowedFeeTokens[feeToken];
        require(address(pf.feed) != address(0), "Unsupported fee token");
        return _getPrice(pf);
    }

    function computeVolumeUSD(address feeToken, uint amount) public view returns(uint volumeUSD) {
        VaultStorage.VaultData storage fs = VaultStorage.load();
        VaultStorage.PriceFeed storage pf = fs.allowedFeeTokens[feeToken];

        //price is in USD with 30decimal precision
        uint ftp = _getPrice(pf);

        (uint v,) = _toUSD(fs, IERC20(feeToken), ftp, amount);
        volumeUSD = v;
    }

    function aumUSD() public view returns(uint usd){
        VaultStorage.VaultData storage fs = VaultStorage.load();

        //for each fee token allowed in the vault
        //move to memory so we're not accessing storage in loop
        IERC20[] memory feeTokens = fs.feeTokens;
        for(uint i=0;i<feeTokens.length;++i) {
            IERC20 ft = IERC20(feeTokens[i]);
            VaultStorage.PriceFeed storage pf = fs.allowedFeeTokens[address(ft)];

            //make sure fee token still active
            //get the price of the asset
            uint price = _getPrice(pf);
            //use it to compute USD value
            (uint _usd,) = _toUSD(fs, ft, price, 0);
            usd += _usd;
        }
    }

    function currentNavUSD() public view returns(uint nav){
        //console.log("--------------- START COMPUTE NAV ---------------------");
        VaultStorage.VaultData storage rs = VaultStorage.load();

        //get the total supply of dxbl tokens
        uint supply = rs.dxbl.totalSupply();

        //get the total USD under management by this vault
        uint aum = aumUSD();

        //if either is 0, the nav is 0
        if(supply == 0 || aum == 0) {
            return 0;
        }

        //supply is 18decs while aum and nav are expressed in USD units
        nav = (aum*1e18) / supply;
      //  console.log("--------------- END COMPUTE NAV ---------------------");
    }

    function assets() public view returns (IComputationalView.AssetInfo[] memory tokens){
        /**
         * RISK: Must limit the fee token count to avoid miner not allowing call due to high
         * gas usage
         */
        VaultStorage.VaultData storage fs = VaultStorage.load();

        //create in-memory structure only for active fee tokens
        tokens = new IComputationalView.AssetInfo[](fs.feeTokens.length);

        //count offset of return tokens
        uint cnt = 0;

        //copy fee tokens in memory to we're not accessing storage in loop
        IERC20[] memory feeTokens = fs.feeTokens;
        for(uint i=0;i<feeTokens.length;++i) {
            IERC20 ft = feeTokens[i];
            VaultStorage.PriceFeed storage pf = fs.allowedFeeTokens[address(ft)];

            //lookup USD price of asset in 30-dec units
            uint price = _getPrice(pf);

            //convert to total usd-precision USD value
            (uint usd, uint bal) = _toUSD(fs, ft, price, 0);

            tokens[cnt] = IComputationalView.AssetInfo({
                token: address(ft),
                balance: bal,
                usdValue: usd,
                usdPrice: (price*LibConstants.USD_PRECISION) / LibConstants.PRICE_PRECISION
            });
            ++cnt;
        }
    }

    function currentMintRateUSD() public view returns (uint rate){
        /**
        * formula for mint rate:
        * startingRate+(startingRate*(ratePerMM*MM_vol))
        */
        VaultStorage.VaultData storage rs = VaultStorage.load();

        uint16 normalizedMMInVolume = uint16(rs.currentVolume / LibConstants.MM_VOLUME);

        //mint rate is a bucket with min/max volume thresholds and establishes how many 
        //percentage points per million to apply to the starting mint rate 
        uint percIncrease = rs.currentMintRate.rate * normalizedMMInVolume;

        //mint rate percentage is expressed in 18-dec units so have to divide that out before adding to base
        rate = rs.baseMintThreshold + ((rs.baseMintThreshold * percIncrease)/1e18);
    }


    function _getPrice(VaultStorage.PriceFeed storage pf) internal view returns (uint) {
        //get latest price
        (   ,
            int256 answer,
            ,
            uint256 updatedAt,
        ) = pf.feed.latestRoundData();

        //make sure price valid
        require(answer > 0, "No price data available");

        //10min buffer around 24hr window for chainlink feed to update prices
        uint stale = block.timestamp - LibConstants.DAY - 600;
        require(updatedAt > stale, "Stale price data");
        return (uint256(answer) * LibConstants.PRICE_PRECISION) / (10**pf.decimals);
    }

    /**
     * Convert an assets total balance to USD
     */
    function _toUSD(VaultStorage.VaultData storage fs, IERC20 token, uint price, uint amt) internal view returns(uint usd, uint bal) {
        bal = amt;
        if(bal == 0) {
            bal = token.balanceOf(address(this));
        }

        //compute usd in raw form (fee-token units + price-precision units) but account for
        //USD precision
        usd = (bal * price)*LibConstants.USD_PRECISION;

        //then divide out the fee token and price-precision units
        usd /= (10**fs.tokenDecimals[address(token)]*LibConstants.PRICE_PRECISION);

    }
}","keccak256":"0x666d8a00f6a0b345b2b32a58cf812dbae97f82fb681df3a48a40ed3ddb41094f","license":"Unlicense"},"contracts/vault/baseContracts/RewardHandler.sol":{"content":"//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.17;

import "../VaultStorage.sol";
import "../IComputationalView.sol";
import "../../common/LibConstants.sol";
import "./ComputationalView.sol";
import "../IRewardHandler.sol";

abstract contract RewardHandler is IRewardHandler {

    modifier onlyDexible() {
        VaultStorage.VaultData storage vs = VaultStorage.load();
        require(msg.sender == vs.dexible, "Unauthorized");
        _;
    }

    /**
     * Modification functions
     */
    function rewardTrader(address trader, address feeToken, uint amount) external override onlyDexible {
        VaultStorage.VaultData storage rs = VaultStorage.load();
        uint volumeUSD = IComputationalView(address(this)).computeVolumeUSD(feeToken, amount);

        //determine the mint rate
        uint rate = IComputationalView(address(this)).currentMintRateUSD();

        //make the volume adjustment to the pool
        _adjustVolume(volumeUSD);

        //get the number of DXBL per $1 of volume
        uint tokens = (volumeUSD*1e18) / rate;

        //we are minter on token, so request to mint tokens
        rs.dxbl.mint(trader, tokens);
    }


    function _adjustVolume(uint volumeUSD) internal {
        VaultStorage.VaultData storage rs = VaultStorage.load();

        //get the current hour
        uint lastTrade = rs.lastTradeTimestamp;

        //record when we last adjusted volume
        rs.lastTradeTimestamp = block.timestamp;
        uint newVolume = volumeUSD;
        if(lastTrade > 0 && lastTrade <= (block.timestamp - LibConstants.DAY)) {
            delete rs.hourlyVolume;
        } else {
            //otherwise, since we never rolled over 24hrs, just delete the volume
            //that accrued 24hrs ago
            uint hr = (block.timestamp % LibConstants.DAY) / LibConstants.HOUR;
            uint slot = 0;
            //remove guard for some efficiency gain
            unchecked{slot = (hr+1)%24; }

            //get the volume bin 24hrs ago by wrapping around to next hour in 24hr period
            uint yesterdayTotal = rs.hourlyVolume[slot];

            //if we get called multiple times in the block, the same hourly total
            //would be deducted multiple times. So we reset it here so that we're 
            //not deducting it multiple times in the hour. Only the first deduction
            //will be applied and 0'd out.
            rs.hourlyVolume[slot] = 0;

            //add new volume to current hour bin
            rs.hourlyVolume[hr] += volumeUSD;

            //manipulate volume in memory not storage
            newVolume = rs.currentVolume + volumeUSD;

            //Remove volume from 24hr's ago if there was anything
            if(yesterdayTotal > 0) {
                //note that because currentVolume includes yesterday's, then this subtraction 
                //is safe.
                newVolume -= yesterdayTotal;
            } 
        }
        rs.currentVolume = newVolume;
        _adjustMintRate(rs, uint16(newVolume / LibConstants.MM_VOLUME));
    }

    /**
     * Make an adjustment to the mint rate if the 24hr volume falls into a new rate bucket
     */
    function _adjustMintRate(VaultStorage.VaultData storage rs, uint16 normalizedMMInVolume) internal {

        VaultStorage.MintRateRange memory mr = rs.currentMintRate;
        //if the current rate bucket's max is less than current normalized volume
        if(mr.maxMMVolume <= normalizedMMInVolume) {
            //we must have increased volume so we have to adjust the rate up
            _adjustMintRateUp(rs, normalizedMMInVolume);
            //otherwise if the current rate's min is more than the current volume
        } else if(mr.minMMVolume >= normalizedMMInVolume) {
            //it means we're trading less volume than the current rate, so we need
            //to adjust it down
            _adjustMintRateDown(rs, normalizedMMInVolume);
        } //else rate stays the same
    }

    /**
     * Increase the minimum volume required to mint a single token
     */
    function _adjustMintRateUp(VaultStorage.VaultData storage rs, uint16 mm) internal {
        VaultStorage.MintRateRange memory mr = rs.currentMintRate;
        while(!_rateInRange(mr,mm)) {
            //move to the next higher rate if one is configured, otherwise stay where we are
            VaultStorage.MintRateRange storage next = rs.mintRateRanges[mr.index + 1];
            if(next.rate == 0) {
                //reached highest rate, that will be the capped rate 
                break;
            }
            mr = next;
        }

        //don't waste gas storing if not changed
        if(rs.currentMintRate.rate != mr.rate) {
            rs.currentMintRate = mr;
        }

    }

    /**
     * Decrease minimum volume required to mint a DXBL token
     */
    function _adjustMintRateDown(VaultStorage.VaultData storage rs, uint16 mm) internal {
        VaultStorage.MintRateRange memory mr = rs.currentMintRate;
        while(!_rateInRange(mr,mm)) {
            if(mr.index > 0) {
                //move to the next higher rate if one is configured, otherwise stay where we are
                VaultStorage.MintRateRange storage next = rs.mintRateRanges[mr.index - 1];
                mr = next;
            } else {
                //we go to the lowest rate then
                break;
            }
        }
        rs.currentMintRate = mr;
    }

    //test to see if volume is range for a rate bucket
    function _rateInRange(VaultStorage.MintRateRange memory range, uint16 mm) internal pure returns (bool) {
        return range.minMMVolume <= mm && mm < range.maxMMVolume;
    }

}","keccak256":"0x9fff5f43b31898ff7b94c0db40ea9e3d3d5259cc75e9fec6ffbbe918b7dbfcc4","license":"Unlicense"},"contracts/vault/baseContracts/StorageView.sol":{"content":"//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.17;

import "../VaultStorage.sol";
import "../IStorageView.sol";

contract StorageView is IStorageView {

    function isFeeTokenAllowed(address token) external view returns (bool){
        VaultStorage.VaultData storage vs = VaultStorage.load();
        return address(vs.allowedFeeTokens[token].feed) != address(0);
    }

    function discountBps() external view returns(uint32) {
        return VaultStorage.load().dxbl.discountPerTokenBps();
    }

    function dailyVolumeUSD() external view returns(uint) {
        return VaultStorage.load().currentVolume;
    }

    function paused() external view returns (bool) {
        return VaultStorage.load().paused;
    }

    function adminMultiSig() external view returns (address) {
        return VaultStorage.load().adminMultiSig;
    }

    function dxbl() external view returns (address) {
        return address(VaultStorage.load().dxbl);
    }

    function dexible() external view returns (address) {
        return VaultStorage.load().dexible;
    }

    function wrappedNativeToken() external view returns (address) {
        return VaultStorage.load().wrappedNativeToken;
    }

    function timelockSeconds() external view returns (uint32) {
        return VaultStorage.load().timelockSeconds;
    }

    function baseMintThreshold() external view returns (uint) {
        return VaultStorage.load().baseMintThreshold;
    }
}","keccak256":"0x544dc003629a51bf7e383e91d98ab9baf516edf9092a7515622877b7f0a09cb8","license":"Unlicense"},"contracts/vault/baseContracts/V1Migration.sol":{"content":"//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.17;

import "../V1Migrateable.sol";

abstract contract V1Migration is V1Migrateable {

    modifier onlyAdmin() {
        VaultStorage.VaultData storage vs = VaultStorage.load();
        require(msg.sender == vs.adminMultiSig, "Unauthorized");
        _;
    }

    function scheduleMigration(V1MigrationTarget newVault) public onlyAdmin {
        VaultStorage.VaultData storage vs = VaultStorage.load();
        vs.migrateAfterTime = block.timestamp + vs.timelockSeconds;
        emit MigrationScheduled(address(newVault), vs.migrateAfterTime);
    }

    function cancelMigration() public onlyAdmin {
        VaultStorage.VaultData storage vs = VaultStorage.load();
        delete vs.migrateAfterTime;
        address addr = vs.pendingMigrationTarget;
        delete vs.pendingMigrationTarget;
        emit MigrationCancelled(addr);
    }

    function canMigrate() public view returns (bool) {
         VaultStorage.VaultData storage vs = VaultStorage.load();
         return vs.pendingMigrationTarget != address(0) &&
                vs.migrateAfterTime > 0 &&
                vs.migrateAfterTime < block.timestamp;
    }

    /**
     * Migrate the state of the vault to a new version. This will transfer all current state
     * and transfer fee token balances as well. Note that 
     */
    function migrateV1() external {
        require(this.canMigrate(), "Cannot migrate yet");

        VaultStorage.VaultData storage vs = VaultStorage.load();
        V1MigrationTarget target = V1MigrationTarget(vs.pendingMigrationTarget);

        //pause all operations
        vs.paused = true;

        //transfer token balances
        IERC20[] storage tokens = vs.feeTokens;
        for(uint i=0;i<tokens.length;++i) {
            uint b = tokens[i].balanceOf(address(this));
            tokens[i].transfer(address(target), b);
        }

        //tell DXBL that there is a new minter
        vs.dxbl.setNewMinter(address(target));

        //create transfer info
        VaultStorage.VaultMigrationV1 memory v1 = VaultStorage.VaultMigrationV1({
            //current daily volume adjusted each hour
            currentVolume: vs.currentVolume,

            //last time traded in this vault
            lastTradeTimestamp: vs.lastTradeTimestamp,

            //hourly volume totals accumulated in last 24 hrs. 
            hourlyVolume: vs.hourlyVolume,

            currentMintRate: vs.currentMintRate
        });

        //ask target to migrate from this vault state
        target.migrationFromV1(v1);

        emit VaultMigrated(address(target));

        //then tear down this version
        address payable addr = payable(address(target));
        selfdestruct(addr);
    }
}","keccak256":"0xb3181e93c5c92d6b00db9862ddf3dce43c276cadbc296bce866a17ce72cfbf84","license":"Unlicense"}},"version":1}

